import { Component, Injectable } from '@angular/core';
import { GridMatrix } from '../models/gridMatrix';


@Component({
  selector: 'dungeon',
  templateUrl: './dungeon.component.html',
  styleUrls: ['./dungeon.component.css']
})
export class DungeonComponent {

  woodAmount = 0;
  woodIncrement = 1;

  items = ['test', 'test2', 'test3', 'test4'];

  gridMatrix: GridMatrix;


  constructor(gridMatrix: GridMatrix) {
    this.gridMatrix = gridMatrix;
  }



  ngOnInit() {
    let that = this;



  }





  getClass(tile) {
    let styleClass = 'tile';

    if (!tile.explored) {
      //return 'unexplored-tile'
    }

    switch (tile.contents) {
      case 'player':
        styleClass = 'player-tile'
        break;
      case 'wall':
        styleClass = 'wall-tile'
        break;
      default:
        break;
    }

    return styleClass;

  }

  onClick(clickedTile) {

    if (clickedTile.explored && clickedTile.walkable) {
      this.gridMatrix.clickedTile(clickedTile);
    }
  }

  getImage(tile) {
    let returnString = '';
    if (tile.contents) {
      switch (tile.contents) {
        case 'wall':
          returnString = 'Wall'
          break;
        case 'player':
          returnString = 'HeroBase'
          break;
        case 'empty':
          //returnString = 'Dirt'
          break;
        case 'gold':
          returnString = 'Gold'
          break;
        case 'attackBoost':
          returnString = 'Attackboost'
          break;
        case 'healthBoost':
          returnString = 'HPboost'
          break;
        case 'manaBoost':
          returnString = 'MPboost'
          break;

        default:
          break;
      }
    }
    return returnString;
  }











}
<ul>
    <div *ngFor="let row of gridMatrix.tileArray; let i = index" class="tileRow">
        <div *ngFor="let tile of gridMatrix.tileArray[i]; let j = index" class="tile" 
        [ngClass]="getClass(tile)" (click)="onClick(tile)">
        <img src='{{ "assets/images/default/" + getImage(tile) + ".png" }}' width="40" height="40">
        </div>
    </div>
</ul>
