import { Injectable } from "@angular/core";
import { Tile } from "./tile";
import * as _ from 'lodash';

@Injectable()
export class GridMatrix {
    tileArray;

    //20 by 20

    constructor() {

        let tiles = [];
        let initialXCoord = Math.random() * 16 + 2;
        let initialYCoord = Math.random() * 16 + 2;
        tiles.push(new Tile(true, false, false, true, 'player', initialXCoord, initialYCoord))

        // walkable empty tiles
        for (let i = 0; i < 180; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }

        // Spells
        for (let i = 0; i < 5; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // Gold
        for (let i = 0; i < 7; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // stat boosts
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // stores
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // churches
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // potions
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }

        // level 1
        for (let i = 0; i < 10; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 2
        for (let i = 0; i < 5; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 3
        for (let i = 0; i < 4; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 4, 5, 6, 7, 8
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 9
        for (let i = 0; i < 2; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 10

        // explorable wall tiles
        for (let i = 0; i < 170; i++) {
            let newtile = new Tile(false, false, true, true);
            tiles.push(newtile);
        }

        // unexplorable wall tiles
        for (let i = 0; i < 50; i++) {
            let newtile = new Tile(false, false, true, false);
            tiles.push(newtile);
        }

        let remaining = 400 - tiles.length;
        // walkable empty tiles
        for (let i = 0; i < remaining; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }



        let walkableList = _.filter(tiles, function (tile) {
            return tile.walkable && tile.xCoord == null && tile.yCoord == null
        });

        let walkableCoords = this.getWalkablePath(initialXCoord, initialYCoord, walkableList.length);

        tiles.forEach(tile => {

        })


        this.tileArray = tiles;
    }



    private getWalkablePath(xCoord, yCoord, tileNumebr) {
        let tileCoords = [{xCoord: xCoord, yCoord: yCoord}];

        tileCoords.push({xCoord: xCoord + 1, yCoord: yCoord});
        tileCoords.push({xCoord: xCoord, yCoord: yCoord + 1});
        tileCoords.push({xCoord: xCoord + 1, yCoord: yCoord + 1});
        tileCoords.push({xCoord: xCoord - 1, yCoord: yCoord});
        tileCoords.push({xCoord: xCoord, yCoord: yCoord - 1});
        tileCoords.push({xCoord: xCoord - 1, yCoord: yCoord - 1});
        tileCoords.push({xCoord: xCoord + 1, yCoord: yCoord - 1});
        tileCoords.push({xCoord: xCoord - 1, yCoord: yCoord + 1});

        for(let i = 0; i < tileNumebr-9; i++){

            let currentCoord = this.getRandomCoordinates(tileCoords);

        }





    }





    getRandomCoordinates(coordinates){
        let currentCoordIndex = Math.random() * coordinates.length;
        let currentCoord = coordinates[currentCoordIndex]

        let surrounded = _.includes(coordinates, function(coordinate){
            return coordinate == {xCoord: currentCoord.xCoord+1, yCoord: currentCoord.yCoord} ||
            coordinate == {xCoord: currentCoord.xCoord, yCoord: currentCoord.yCoord+1} ||
            coordinate == {xCoord: currentCoord.xCoord+1, yCoord: currentCoord.yCoord+1} ||
            coordinate == {xCoord: currentCoord.xCoord-1, yCoord: currentCoord.yCoord} ||
            coordinate == {xCoord: currentCoord.xCoord, yCoord: currentCoord.yCoord-1} ||
            coordinate == {xCoord: currentCoord.xCoord-1, yCoord: currentCoord.yCoord-1} ||
            coordinate == {xCoord: currentCoord.xCoord+1, yCoord: currentCoord.yCoord-1} ||
            coordinate == {xCoord: currentCoord.xCoord-1, yCoord: currentCoord.yCoord+1}
        })

        if(surrounded){
            return this.getRandomCoordinates(coordinates);
        } else{
            return currentCoord;
        }

    }



}
