import { Injectable } from "@angular/core";
import { Tile } from "./tile";
import * as _ from 'lodash';

@Injectable()
export class GridMatrix {
    tileArray;

    //20 by 20

    constructor() {

        let tiles = [];
        let xCoord  = Math.random() * 16+2;
        let yCoord  = Math.random() * 16+2;
        tiles.push(new Tile(true, false, false, true, 'player', xCoord, yCoord))

        // walkable empty tiles
        for (let i = 0; i < 180; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }

        // Spells
        for (let i = 0; i < 5; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // Gold
        for (let i = 0; i < 7; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // stat boosts
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // stores
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // churches
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // potions
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }

        // level 1
        for (let i = 0; i < 10; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 2
        for (let i = 0; i < 5; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 3
        for (let i = 0; i < 4; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 4, 5, 6, 7, 8
        for (let i = 0; i < 3; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 9
        for (let i = 0; i < 2; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }
        // level 10

        // explorable wall tiles
        for (let i = 0; i < 170; i++) {
            let newtile = new Tile(false, false, true, true);
            tiles.push(newtile);
        }

        // unexplorable wall tiles
        for (let i = 0; i < 50; i++) {
            let newtile = new Tile(false, false, true, false);
            tiles.push(newtile);
        }

        let remaining = 400 - tiles.length;
        // walkable empty tiles
        for (let i = 0; i < remaining; i++) {
            let newtile = new Tile(false, false, false, true);
            tiles.push(newtile);
        }



        let test = _.filter(tiles, function(tile){
            return tile.walkable && tile.xCoord == null && tile.yCoord == null
        });

        console.count(test)

        tiles.forEach(tile =>{
        })
        

        this.tileArray = tiles;
    }







}
