import { Injectable } from "@angular/core";

@Injectable()
export class Character {
  //stats
  name: string;
  race: string;
  class: string;
  level: number;
  experience: number;
  gold: number;
  piety: number;
  healthPotions: number;
  manaPotions: number;
  strength: number;
  bonusStrength: number;
  currentHealth: number;
  currentMana: number;
  baseHealth: number;
  baseMana: number;
  baseHealthRegen: number
  manaRegen: number;
  healthPotionGain: number;
  manaPotionGain: number;
  permaFirstStrike: boolean;


  //temp positive statuses
  firstStrike: boolean;
  bonusStrike: boolean;
  deathDefiance: boolean;
  petrification: boolean;

  //temp negative statuses
  poisoned: boolean;
  manaBurn: boolean;
  weakened: boolean;




  constructor() {
    this.name = 'Jared'
    this.piety = 20
    this.gold = 13
    this.manaPotions = 1
    this.healthPotions = 1;
    this.strength = 5;
    this.bonusStrength = 0;
    this.class = 'Fighter'
    this.race = 'Human'
    this.baseHealth = 10
    this.baseMana = 12
    this.currentHealth = 15
    this.currentMana = 12
    this.baseHealthRegen = 1
    this.manaRegen = 1
    this.level = 3
    this.experience = 7;
    this.healthPotionGain = 0.38;
    this.manaPotionGain = 0.35;
    this.permaFirstStrike = false;;

  }


  getMaxHealth(){
    return this.baseHealth * this.level;
  }

  getCurrentAttack(){
    return this.strength + this.bonusStrength
  }

  getMaxExperience(){
    return this.level * 5
  }

  getHealthRegen(){
    return this.level * this.baseHealthRegen;
  }

  exploredSquare(){
    (this.currentHealth + this.getHealthRegen() ) > this.getMaxHealth() ? this.currentHealth = this.getMaxHealth() : this.currentHealth += this.getHealthRegen();
    (this.currentMana + this.manaRegen ) > this.baseMana ? this.currentMana = this.baseMana : this.currentMana += this.manaRegen;

  }

}
