import { Injectable } from "@angular/core";

@Injectable()
export class Character {
  //stats
  name: string;
  race: string;
  class: string;
  level: number;
  experience: number;
  gold: number;
  piety: number;
  healthPotions: number;
  manaPotions: number;
  strength: number;
  bonusStrength: number;
  currentHealth: number;
  currentMana: number;
  baseHealth: number;
  baseMana: number;
  healthRegen: number;
  manaRegen: number;
  healthPotionGain: number;
  manaPotionGain: number;
  permaFirstStrike: boolean;


  //temp positive statuses
  firstStrike: boolean;
  bonusStrike: boolean;
  deathDefiance: boolean;
  petrification: boolean;

  //temp negative statuses
  poisoned: boolean;
  manaBurn: boolean;
  weakened: boolean;




  constructor() {
    this.piety = 0;

  }


  getMaxHealth(){
    return this.baseHealth * this.level;
  }

  getCurrentAttack(){
    return this.strength + this.bonusStrength
  }

  getMaxExperience(){
    return this.level * 5
  }

  exploredSquare(){
    this.currentHealth
  }

}
