import { Component, Injectable } from '@angular/core';
import { Character } from '../models/character';
import { GridMatrix } from '../models/gridMatrix';
import { Tile } from '../models/tile';


@Component({
  selector: 'dungeon',
  templateUrl: './dungeon.component.html',
  styleUrls: ['./dungeon.component.css']
})
export class DungeonComponent {

  items = ['test', 'test2', 'test3', 'test4'];

  gridMatrix: GridMatrix;
  character: Character;
  relevantTile: Tile;

  constructor(gridMatrix: GridMatrix) {
    this.character = new Character();
    this.gridMatrix = new GridMatrix(this.character);
    this.relevantTile = this.gridMatrix.getPlayerTile();

  }



  ngOnInit() {
    let that = this;



  }

  tileHovered(hoveredTile) {
    let playerTile = this.gridMatrix.getPlayerTile();

    if (hoveredTile.hasInfo() && hoveredTile.explored) {
      this.relevantTile = hoveredTile;
    } else if (this.relevantTile != null && !this.relevantTile.playerAndContent && !playerTile.playerAndContent && hoveredTile.explored) {
      this.relevantTile = hoveredTile;
    } else {
      this.relevantTile = playerTile;
    }
  }



  // getClass(tile) {
  //   let styleClass = 'tile';

  //   if (!tile.explored) {
  //     return 'unexplored-tile'
  //   }

  //   switch (tile.contents) {
  //     case 'player':
  //       styleClass = 'player-tile'
  //       break;
  //     case 'wall':
  //       styleClass = 'wall-tile'
  //       break;
  //     default:
  //       break;
  //   }

  //   return styleClass;

  // }

  onClick(clickedTile) {

    if (clickedTile.explored && clickedTile.walkable) {
      let updateInfoPanel = this.gridMatrix.clickedTile(clickedTile, this.character);

      if (updateInfoPanel) {
        this.relevantTile = clickedTile;
      }
    }
  }

  getImage(tile) {
    let returnString = '';
    if (tile.contents != null) {
      switch (tile.contents) {
        case 'wall':
          returnString = 'Wall'
          break;
        case 'empty':
          //returnString = 'Dirt'
          break;
        case 'gold':
          returnString = 'Gold'
          break;
        case 'strengthBoost':
          returnString = 'Attackboost'
          break;
        case 'healthBoost':
          returnString = 'HPBoost'
          break;
        case 'manaBoost':
          returnString = 'MPBoost'
          break;
        case 'shop':
          returnString = 'Shop'
          break;
        case 'altar':
          returnString = 'Altar'
          break;
        case 'healthPotion':
          returnString = 'HealthPotion'
          break;
        case 'manaPotion':
          returnString = 'ManaPotion'
          break;
        case 'spell':
          returnString = 'G_Generic'
          break;
        case 'enemy1':
          returnString = 'MeatMan'
          break;

        default:
          break;
      }
      if (tile.contents.pngString != null) {
        returnString = tile.contents.pngString;
      }
    }
    return returnString;
  }











}
