import { Component, Injectable } from '@angular/core';
import { Character } from 'src/app/models/character';


@Component({
  selector: 'infoPanel',
  templateUrl: './infoPanel.component.html',
  styleUrls: ['./infoPanel.component.css']
})
export class InfoPanelComponent {

  @Input() character: Character;
  woodAmount = 0;
  woodIncrement = 1;

  constructor(character:Character) {
    character.name = 'Jared'
    character.piety = 20
    character.gold = 13
    character.manaPotions = 1
    character.healthPotions = 1;

    this.character = character;
  }

  ngOnInit() {
    let that = this;

  }

  getImage(tile) {
    let returnString = '';
    if (tile.contents) {
      switch (tile.contents) {
        case 'wall':
          returnString = 'Wall'
          break;
        case 'player':
          returnString = 'HeroBase'
          break;
        case 'empty':
          //returnString = 'Dirt'
          break;
        case 'gold':
          returnString = 'Gold'
          break;
        case 'attackBoost':
          returnString = 'Attackboost'
          break;
        case 'healthBoost':
          returnString = 'HPBoost'
          break;
        case 'manaBoost':
          returnString = 'MPBoost'
          break;
        case 'shop':
          returnString = 'Shop'
          break;
        case 'altar':
          returnString = 'Altar'
          break;
        case 'healthPotion':
          returnString = 'HealthPotion'
          break;
        case 'manaPotion':
          returnString = 'ManaPotion'
          break;
        case 'spell':
          returnString = 'G_Generic'
          break;
        case 'enemy1':
          returnString = 'MeatMan'
          break;

        default:
          break;
      }
    }
    return returnString;
  }











}
