import { Injectable } from "@angular/core";

@Injectable()
export class Spell {
  name: string;
  pngString: string;
  description: string;
  manaCost: number;


  constructor(name: string) {

    switch (name) {
      case 'fireBall':
        this.name = 'Fire Ball';
        this.pngString = 'G_Fireball';
        this.description = 'Hits a monster with a fireball, causing 4 points of damage per player level';
        this.manaCost = 6;
        break;
      case 'poison':
        this.name = 'Poison';
        this.pngString = 'G_Poison';
        this.description = "Inflicts a monster with poison, preventing it from regenerating health as you explore. Will wear off if they're attacked. Undead are immune.";
        this.manaCost = 5;
        break;
      case 'bloodMagic':
        this.name = 'Blood Magic';
        this.pngString = 'G_Blood';
        this.description = '';
        this.manaCost = 0;
        break;
      case 'might':
        this.name = 'Might';
        this.pngString = 'G_Might';
        this.description = '';
        this.manaCost = 2;
        break;
      case 'deathDefy':
        this.name = 'Death Defy';
        this.pngString = 'G_KillProtect';
        this.description = '';
        this.manaCost = 10;
        break;
      case 'destroyWall':
        this.name = 'Destroy Wall';
        this.pngString = 'G_Endwall';
        this.description = '';
        this.manaCost = 8;
        break;
      case 'firstStrike':
        this.name = 'First Strike';
        this.pngString = 'G_FirstStrike';
        this.description = '';
        this.manaCost = 3;
        break;
      case 'heal':
        this.name = 'Heal';
        this.pngString = 'G_Heal';
        this.description = '';
        this.manaCost = 3;
        break;
      case 'petrify':
        this.name = 'Petrify';
        this.pngString = 'G_Petrify';
        this.description = '';
        this.manaCost = 5;
        break;
      case 'reveal':
        this.name = 'Reveal';
        this.pngString = 'G_Reveal';
        this.description = '';
        this.manaCost = 3;
        break;
      case 'teleportMonster':
        this.name = 'Teleport Monster';
        this.pngString = 'G_TeleMonster';
        this.description = '';
        this.manaCost = 10;
        break;
      case 'teleport':
        this.name = 'Teleport';
        this.pngString = 'G_TeleSelf';
        this.description = '';
        this.manaCost = 6;
        break;
      case 'summon':
        this.name = 'Summon';
        this.pngString = 'G_Summon';
        this.description = '';
        this.manaCost = 6;
        break;
      default:
        break;
    }



  }

}
