import { Injectable } from "@angular/core";

@Injectable()
export class Enemy {
  //stats
  name; string;
  type: string;
  level: number;
  strength: number;
  bonusStrength: number;
  currentHealth: number;
  baseHealth: number;
  baseHealthRegen: number
  permaFirstStrike: boolean;

  deathDefiance: boolean;
  petrification: boolean;

  // negative statuses
  poisoned: boolean;
  weakened: boolean;
  vulernable: boolean;
  physicalResist: boolean;
  magicResist: boolean;

  // metaData
  pngString: String;
  levelNumberWidth: number;
  levelNumberHeight: number;




  constructor() {
    this.pngString = 'MeatMan'
    this.name = 'Meat Man'
    this.type = 'Normal'
    this.level = 4
    this.strength = 5;
    this.bonusStrength = 0;
    this.currentHealth = 15
    this.baseHealth = 10
    this.baseHealthRegen = 1
    this.permaFirstStrike = false;
    this.levelNumberWidth = 6;
    this.levelNumberHeight = 12;

  }


  getMaxHealth() {
    return this.baseHealth * this.level;
  }

  getCurrentAttack() {
    return this.strength + this.bonusStrength
  }

  getHealthRegen() {
    return this.level * this.baseHealthRegen;
  }

}
