import { Injectable } from "@angular/core";

@Injectable()
export class Spell {
  name: string;
  pngString: string;
  description: string;
  manaCost: number;


  constructor(name: string) {

    switch (name) {
      case 'fireBall':
        this.name = 'Fire Ball';
        this.pngString = 'G_Fireball';
        break;
      case 'poison':
        this.name = 'Fire';
        this.pngString = 'G_Poison';
        break;
      case 'bloodMagic':
        this.name = 'Fire';
        this.pngString = 'G_Blood';
        break;
      case 'might':
        this.name = 'Fire';
        this.pngString = 'G_Might';
        break;
      case 'deathDefy':
        this.name = 'Fire';
        this.pngString = 'G_KillProtect';
        break;
      case 'destroyWall':
        this.name = 'Fire';
        this.pngString = 'G_Endwall';
        break;
      case 'firstStrike':
        this.name = 'Fire';
        this.pngString = 'G_FirstStrike';
        break;
      case 'heal':
        this.name = 'Fire';
        this.pngString = 'G_Heal';
        break;
      case 'petrify':
        this.name = 'Fire';
        this.pngString = 'G_Petrify';
        break;
      case 'reveal':
        this.name = 'Fire';
        this.pngString = 'G_Reveal';
        break;
      case 'teleportMonster':
        this.name = 'Fire';
        this.pngString = 'G_TeleMonster';
        break;
      case 'teleport':
        this.name = 'Fire';
        this.pngString = 'G_TeleSelf';
        break;
      case 'summon':
        this.name = 'Fire';
        this.pngString = 'G_Summon';
        break;
      default:
        break;
    }



  }

}
