import { Component, Injectable } from '@angular/core';


@Component({
  selector: 'infoPanel',
  templateUrl: './infoPanel.component.html',
  styleUrls: ['./infoPanel.component.css']
})
export class InfoPanelComponent {

  woodAmount = 0;
  woodIncrement = 1;

  constructor() {
  }

  ngOnInit() {
    let that = this;

  }

  getImage(tile) {
    let returnString = '';
    if (tile.contents) {
      switch (tile.contents) {
        case 'wall':
          returnString = 'Wall'
          break;
        case 'player':
          returnString = 'HeroBase'
          break;
        case 'empty':
          //returnString = 'Dirt'
          break;
        case 'gold':
          returnString = 'Gold'
          break;
        case 'attackBoost':
          returnString = 'Attackboost'
          break;
        case 'healthBoost':
          returnString = 'HPBoost'
          break;
        case 'manaBoost':
          returnString = 'MPBoost'
          break;
        case 'shop':
          returnString = 'Shop'
          break;
        case 'altar':
          returnString = 'Altar'
          break;
        case 'healthPotion':
          returnString = 'HealthPotion'
          break;
        case 'manaPotion':
          returnString = 'ManaPotion'
          break;
        case 'spell':
          returnString = 'G_Generic'
          break;
        case 'enemy1':
          returnString = 'MeatMan'
          break;

        default:
          break;
      }
    }
    return returnString;
  }











}
